    private void fn_guarda() {
    if (txt_nombre.getText().equals("") || txt_ruta.getText().equals("") ||
        txt_marca.getText().equals("") || txt_descripcion.getText().equals("") ||
        txt_modelo.getText().equals("") || txt_placa.getText().equals("")) {
        JOptionPane.showMessageDialog(null, "Obligatorio completar todos los campos");
    } else {
        posicion=0;
        sw = false;
        for (int i=0;i<veiculos.size();i++) {
            if (veiculos.get(i).getPlacast().equals(txt_placa.getText())) {
                sw = true;
            }
        }

        if (sw == false) {
            veiculos.add(new cls_regitroV(txt_nombre.getText(),
                txt_ruta.getText(), txt_marca.getText(),
                txt_descripcion.getText(), txt_modelo.getText(),
                txt_placa.getText()));

            JOptionPane.showMessageDialog(null, "Registro Exitoso");
        } else {
            JOptionPane.showMessageDialog(null, "Este registro ya existe");
        }
    }
}
      
    private void btn_guardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_guardarActionPerformed
     fn_guarda();
    }//GEN-LAST:event_btn_guardarActionPerformed

