    private void fn_consultar() {
    for (int i = 0; i < veiculos.size(); i++) {
        if (veiculos.get(i).getPlacast().equals(txt_consulta_placa.getText())) {
            JOptionPane.showMessageDialog(null,
                "Transporte:\n" +
                "Placa: " + veiculos.get(i).getPlacast() + "\n" +
                "Nombre del conductor: " + veiculos.get(i).getNombre_conduString()+ "\n" +
                "Descripción: " + veiculos.get(i).getDescripcionst() + "\n" +
                "Ruta: " + veiculos.get(i).getRutast() + "\n" +
                "Marca: " + veiculos.get(i).getMarcast() + "\n" +
                "Modelo: " + veiculos.get(i).getModelost()
            );
            break;
        }
    }
}
         
    private void btn_consultarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_consultarActionPerformed
        fn_consultar();
    }//GEN-LAST:event_btn_consultarActionPerformed

